mapper.select:
select 
	count(cBill_C) 
from 
	${com}.d_t_food_bill
where DBUSINESS >= #{startTime}
AND DBUSINESS <= #{endTime}

select 
	count(cBill_C)
from ${com}.d_t_food_bill a
where a.DBUSINESS >= #{startTime}
AND a.DBUSINESS <= #{endTime}

select
	tt.cpay_n
from
(
select
	@rownum:=@rownum+1 x,
	t.CPAY_N
from(
	SELECT
		p.CPAY_N,
		@rownum:=0
	from 
		${com}.d_t_bill_pay p
	WHERE 
		p.CPAY_C = #{payC}
) t
)tt
where
tt.x = 1

SELECT
	DISTINCT date_format(DBUSINESS, '%Y-%m-%d') settletime
FROM
	${com}.d_t_food_bill b
WHERE
	DBUSINESS >= #{startTime}
AND DBUSINESS <= #{endTime}
ORDER BY
	date_format(DBUSINESS, '%Y-%m-%d')
limit #{startRow},#{endRow}
