where DBUSINESS >= #{startTime}
and DBUSINESS <= #{endTime}
and cBranch_C = #{shopC} 
and cTable_N = #{tableN}
limit #{startRow}, #{pageNum}

	select count(*) from ${com}.d_t_food_bill
	where DBUSINESS >= #{startTime}
	and DBUSINESS <= #{endTime}

        and cCreateMan like #{createMan}  


