mapper.select:
SELECT CASE WHEN SUM(p.nPayAmt) IS NULL THEN 0 ELSE SUM(p.nPayAmt) END payAmt FROM ${com}.d_t_food_bill b LEFT JOIN ${com}.d_t_bill_pay p ON b.cBill_C = p.cBill_C WHERE DBUSINESS >=#{startDate} AND DBUSINESS <=#{endDate}
AND b.cBranch_C LIKE #{shopC}
AND b.cPeriod_N LIKE #{period}
AND b.cShift_N LIKE #{shift}
AND b.cTable_N LIKE #{table}
AND p.cPay_N IS NOT NULL ORDER BY b.cBill_C, p.nPayAmt

SELECT
	COUNT(*)
FROM
	${com}.d_t_food_bill b
LEFT JOIN
	${com}.d_t_bill_pay p
ON
	b.cBill_C = p.cBill_C
WHERE
	DBUSINESS >=#{startDate}
AND DBUSINESS <=#{endDate}

AND b.cBranch_C LIKE #{shopC}


AND b.cPeriod_N LIKE #{period}


AND b.cShift_N LIKE #{shift}


AND b.cTable_N LIKE #{table}

AND p.cPay_N IS NOT NULL

SELECT b.CBill_C billC, cPay_N payN, sUnit unit, p.nPayAmt payAmt, date_format(dBusiness, '%Y-%m-%d') businessDate, b.cTable_N tableN, b.cPeriod_N period, b.cShift_N shift, cBranch_C shopC, cBranch_N shopN, p.cVIP_c vipC, p.cVIP_n vipN FROM ${com}.d_t_food_bill b LEFT JOIN ${com}.d_t_bill_pay p ON b.cBill_C = p.cBill_C WHERE DBUSINESS >=#{startDate} AND DBUSINESS <=#{endDate}
AND b.cBranch_C LIKE #{shopC}
AND b.cPeriod_N LIKE #{period}
AND b.cShift_N LIKE #{shift}
AND b.cTable_N LIKE #{table}
AND p.cPay_N IS NOT NULL ORDER BY b.cBill_C, p.nPayAmt limit #{startRow},#{endRow}
