mapper.select:
SELECT CASE WHEN SUM(price) IS NULL THEN 0 ELSE SUM(price) END price, CASE WHEN SUM(returnAmount) IS NULL THEN 0 ELSE SUM(returnAmount) END returnAmount, CASE WHEN SUM(returnAmt) IS NULL THEN 0 ELSE SUM(returnAmt) END returnAmt FROM ( SELECT date_format(DBUSINESS, '%Y-%m-%d') businessDate, b.cBill_C billC, b.cTable_N tableN, b.cPeriod_N period, b.cShift_N shift, bs.cFood_C foodC, bs.sUnit unit, bs.nPrc price, bs.nQty returnAmount, bs.nAmt returnAmt, bs.sRetSendRemark returnWhy, bs.cPresentBackMan backMan, bs.eSuitFlag suitFlag, b.cBranch_C shopC FROM ${com}.d_t_food_bill b LEFT JOIN ${com}.d_t_food_Bills bs ON b.cBill_C = bs.cBill_C WHERE b.CBRANCH_C like #{shopC}
and b.CPERIOD_N like #{period}
and b.CSHIFT_N like #{shift}
AND bs.eRetSendFlag = '退品' AND bs.eSuitFlag <> '套餐子项' AND date_format(DBUSINESS, '%Y-%m-%d') IN ( SELECT date_format(DBUSINESS, '%Y-%m-%d') FROM ${com}.d_t_food_bill d WHERE DBUSINESS >= #{startDate} AND DBUSINESS <= #{endDate} GROUP BY date_format(DBUSINESS, '%Y-%m-%d') ) GROUP BY date_format(DBUSINESS, '%Y-%m-%d'), b.cTable_N , b.cBill_C , b.cPeriod_N , b.cShift_N , bs.cFood_C , bs.sUnit , bs.nPrc , bs.nQty , bs.nAmt , bs.sRetSendRemark , bs.cPresentBackMan , bs.eSuitFlag , b.cBranch_C )t

SELECT COUNT(*) FROM ( SELECT date_format(DBUSINESS, '%Y-%m-%d') businessDate, b.cBill_C billC, b.cTable_N tableN, b.cPeriod_N period, b.cShift_N shift, bs.cFood_C foodC, bs.sUnit unit, bs.nPrc price, bs.nQty returnAmount, bs.nAmt returnAmt, bs.sRetSendRemark returnWhy, bs.cPresentBackMan backMan, bs.eSuitFlag suitFlag, b.cBranch_C shopC FROM ${com}.d_t_food_bill b LEFT JOIN ${com}.d_t_food_Bills bs ON b.cBill_C = bs.cBill_C WHERE b.CBRANCH_C like #{shopC}
and b.CPERIOD_N like #{period}
and b.CSHIFT_N like #{shift}
AND bs.eRetSendFlag = '退品' AND bs.eSuitFlag <> '套餐子项' AND date_format(DBUSINESS, '%Y-%m-%d') IN ( SELECT date_format(DBUSINESS, '%Y-%m-%d') FROM ${com}.d_t_food_bill d WHERE DBUSINESS >= #{startDate} AND DBUSINESS <= #{endDate} GROUP BY date_format(DBUSINESS, '%Y-%m-%d') ) GROUP BY date_format(DBUSINESS, '%Y-%m-%d'), b.cTable_N , b.cBill_C , b.cPeriod_N , b.cShift_N , bs.cFood_C , bs.sUnit , bs.nPrc , bs.nQty , bs.nAmt , bs.sRetSendRemark , bs.cPresentBackMan , bs.eSuitFlag , b.cBranch_C )t

SELECT date_format(DBUSINESS, '%Y-%m-%d') businessDate, b.cBill_C billC, b.cTable_N tableN, b.cPeriod_N period, b.cShift_N shift, bs.cFood_C foodC, bs.cFood_N foodN, bs.sUnit unit, bs.nPrc price, bs.nQty returnAmount, bs.nAmt returnAmt, bs.sRetSendRemark returnWhy, bs.cPresentBackMan backMan, bs.eSuitFlag suitFlag, b.cBranch_C shopC, b.cBranch_N shopN, cLitCls_C smallC, cLitCls_N smallN FROM ${com}.d_t_food_bill b LEFT JOIN ${com}.d_t_food_Bills bs ON b.cBill_C = bs.cBill_C WHERE b.CBRANCH_C LIKE #{shopC}
and b.CPERIOD_N like #{period}
and b.CSHIFT_N like #{shift}
AND bs.eRetSendFlag = '退品' AND bs.eSuitFlag <> '套餐子项' AND date_format(DBUSINESS, '%Y-%m-%d') IN ( SELECT date_format(DBUSINESS, '%Y-%m-%d') FROM ${com}.d_t_food_bill d WHERE DBUSINESS >= #{startDate} AND DBUSINESS <= #{endDate} GROUP BY date_format(DBUSINESS, '%Y-%m-%d') ) GROUP BY date_format(DBUSINESS, '%Y-%m-%d'), b.cTable_N , b.cBill_C , b.cPeriod_N , b.cShift_N , bs.cFood_C , bs.cFood_N , bs.sUnit , bs.nPrc , bs.nQty , bs.nAmt , bs.sRetSendRemark , bs.cPresentBackMan , bs.eSuitFlag , b.cBranch_C , b.cBranch_N , cLitCls_C, cLitCls_N ORDER BY date_format(DBUSINESS, '%Y-%m-%d'), b.cBranch_C, b.cBill_C, b.cTable_N, b.cPeriod_N, b.cShift_N, bs.cFood_C limit #{startRow},#{endRow}
