mapper.select:
select
	tt.shopName
from
(
select
	@rownum:=@rownum+1 x,
	t.shopName
from(
	SELECT
		cBranch_N shopName,
		@rownum:=0
	from 
		${com}.d_t_food_bill
	WHERE 
		cBranch_C = #{shopC}
) t
)tt
where
tt.x = 1

SELECT DISTINCT cTable_N tableN, SUM(nFoodAmt) foodAmt, COUNT(cBranch_C) billNum, SUM(iGuestNum) guestNum FROM ${com}.d_t_food_bill WHERE DBUSINESS >= #{startTime} AND DBUSINESS <= #{endTime}
AND cBranch_C = #{shopC}
GROUP BY cTable_N ORDER BY cTable_N
