mapper.select:
SELECT 
	sum(npayamt)
FROM ${com}.d_t_food_bill
WHERE cbranch_c like #{shopC}
AND dbusiness >= #{startTime}
AND dbusiness <= #{endTime}

SELECT c.cFood_C , c.cFood_N, c.sUnit, ROUND(SUM(c.nQty),2) nQty, CASE WHEN (#{theSum} = 0) THEN 0 ELSE ROUND(SUM(c.nQty) * 10000/ #{theSum},2) END amtTTCNY FROM ${com}.d_t_food_bills c LEFT JOIN ${com}.d_t_food_bill a ON a.cBill_C = c.cBill_C WHERE 1 = 1
AND c.cLitCls_C = #{categoryId}
AND a.cbranch_c like #{shopC}
AND a.DBUSINESS >= #{startTime}
AND a.DBUSINESS >= #{endTime}
GROUP BY c.cFood_C, c.cFood_N, c.sUnit ORDER BY 1
